DeviceProperties.ListenerScriptPacketParser:
VAR jsonResponse := TOSTRING(RECEIVEDBYTES);
VAR whatEd0 := "shellies/shellyem-"+deviceID+"/emeter/0/energy";
VAR whatTo0 := "shellies/shellyem-"+deviceID+"/emeter/0/total";
VAR whatEd1 := "shellies/shellyem-"+deviceID+"/emeter/1/energy";
VAR whatTo1 := "shellies/shellyem-"+deviceID+"/emeter/1/total";
var Ed0 := 0;
var Ed1 := 0;
var To0 := 0;
var To1 := 0;
var c := PARSEJSON(jsonResponse, "Topic");
IF (whatEd0 = PARSEJSON(jsonResponse, "Topic"))
 Ed0 := todouble(parsejson(jsonResponse, "Payload"))/60000;
END
IF (whatTo0 = PARSEJSON(jsonResponse, "Topic"))
 To0 := todouble(parsejson(jsonResponse, "Payload"));
END
IF (whatEd1 = PARSEJSON(jsonResponse, "Topic"))
 Ed1 := todouble(parsejson(jsonResponse, "Payload"))/60000;
END
IF (whatTo1 = PARSEJSON(jsonResponse, "Topic"))
 To1 := todouble(parsejson(jsonResponse, "Payload"));
END

To := To0 + To1;
Ed := Ed0 + Ed1;

