DeviceProperties.ReadSwitchStateScriptPacketParser:
var response := sendhttprequest("/status/gas_sensor");
if response.issuccess
 var c := response.Content;
 var status := parsejson(parsejson(c,"gas_sensor"),"alarm_state");
 switch(status, "none", return(0),
 "mild", return(1),
 "heavy", return(2),
 "test", return(4),
 return(3));
END

