DeviceProperties.ListenerScriptPacketParser:
VAR jsonResponse := TOSTRING(RECEIVEDBYTES);

VAR whatT := "shellies/shellyplug-s-"+deviceID+"/temperature";
IF (whatT = PARSEJSON(jsonResponse, "Topic"))
 temperatureC := PARSEJSON(jsonResponse, "Payload");
END

VAR jsonResponse := TOSTRING(RECEIVEDBYTES);
VAR whatTo := "shellies/shellyplug-s-"+deviceID+"/relay/0/energy";
IF (whatTo = PARSEJSON(jsonResponse, "Topic"))
 To := TODOUBLE(PARSEJSON(jsonResponse, "Payload"))/60000;
END
VAR whatEd := "shellies/shellyplug-s-"+deviceID+"/relay/0/power";
IF (whatEd = PARSEJSON(jsonResponse, "Topic"))
 Ed :=TODOUBLE(PARSEJSON(jsonResponse, "Payload"))/1000;
END

