DeviceProperties.ReadScriptPacketParser:
VAR response := SENDHTTPREQUEST("/rpc/shelly.GetDeviceInfo");
IF response.IsSuccess
  VAR content := response.Content;
  VAR responseHeaders := response.Headers;
  StatusJson := response.Content;
END;

response := SENDHTTPREQUEST("/rpc/Sys.GetStatus");
IF response.IsSuccess
  content := response.Content;
  responseHeaders := response.Headers;
  if(PARSEJSON(response.Content, "$.restart_required") = true, ADDERROR("Restart required"));
END

DeviceProperties.ReadSwitchStateScriptPacketParser:
var x := SENDHTTPREQUEST("/relay/0/ison");
return(parsejson(x.Content, "ison"));



